["Constructor"] = "function (self, unitId, unitFrame, envTable)\n    \n    --usage: color name e.g \"red\" \"yellow\"; color table e.g {1, 0, 0} {1, 1, 0}; hex string e.g. \"#FF0000\" \"FFFF00\"\n    \n    envTable.TargetColor = \"green\"\n    envTable.TargetingMeColor = \"red\"\n    --envTable.TargetingMeEnabled = true\n    envTable.IgnorePlayers = true\n    --envTable.TargetColor = \"#FF00FF\"\n    envTable.TargetColor = {28/255, 255/255, 0/255}\n    \n    \n    local DF = _G [\"DetailsFramework\"]\n    function envTable.UpdateColor (unitFrame)\n        --do not change the color of the personal bar\n        if (not unitFrame.IsSelf) and not (envTable.IgnorePlayers and (unitFrame.ActorType == \"friendlyplayer\" or unitFrame.ActorType == \"enemyplayer\")) then\n            \n            --if this nameplate the current target of the player?\n            if (unitFrame.namePlateIsTarget) then\n                DF:SetFontColor (unitFrame.PlateFrame.ActorNameSpecial, envTable.TargetColor)\n                Plater.SetNameplateColor (unitFrame, envTable.TargetColor)  --rgb\n            elseif envTable.TargetingMeEnabled and UnitIsUnit(unitFrame.targetUnitID, \"player\") then\n                DF:SetFontColor (unitFrame.PlateFrame.ActorNameSpecial, envTable.TargetingMeColor)\n                Plater.SetNameplateColor (unitFrame, envTable.TargetingMeColor)\n            else\n                --refresh the nameplate color\n                --Plater.RefreshNameplateColor (unitFrame)\n                envTable.ResetBigActorNameColor(unitFrame.PlateFrame)\n                local npcColor = Plater.GetNpcColor (unitFrame)\n                if npcColor then\n                    Plater.SetNameplateColor (unitFrame, npcColor)\n                else\n                    Plater.RefreshNameplateColor (unitFrame)\n                end\n            end\n        end\n    end\n    \n    \n    function envTable.ResetBigActorNameColor(plateFrame)\n        local plateConfigs = Plater.db.profile.plate_config[plateFrame.actorType]\n        if (plateFrame.IsFriendlyPlayerWithoutHealthBar) then --not critical code\n            local nameFontString = plateFrame.ActorNameSpecial\n            \n            \n            --format the color if is the same guild, a friend from friends list or color by player class\n            if (Plater.db.profile.plate_config.friendlyplayer.actorname_use_guild_color and plateFrame.playerGuildName == Plater.PlayerGuildName) then\n                --is a guild friend?\n                DF:SetFontColor (nameFontString, unpack(Plater.db.profile.plate_config.friendlyplayer.actorname_guild_color))\n                \n            elseif (Plater.db.profile.plate_config.friendlyplayer.actorname_use_friends_color and Plater.FriendsCache [plateFrame.namePlateUnitName]) then\n                \n                DF:SetFontColor (nameFontString, unpack(Plater.db.profile.plate_config.friendlyplayer.actorname_friend_color))\n                \n            else\n                --isn't friend, check if is showing only the name and if is showing class colors\n                if (Plater.db.profile.plate_config.friendlyplayer.actorname_use_class_color) then\n                    local _, unitClass = UnitClass (plateFrame.namePlateUnitToken)\n                    if (unitClass) then\n                        local color = RAID_CLASS_COLORS [unitClass]\n                        DF:SetFontColor (nameFontString, color.r, color.g, color.b)\n                    else\n                        DF:SetFontColor (nameFontString, plateConfigs.actorname_text_color)\n                    end\n                else\n                    DF:SetFontColor (nameFontString, plateConfigs.actorname_text_color)\n                end\n                \n                plateFrame.isFriend = nil\n            end\n            \n            return\n            \n        elseif (plateFrame.IsNpcWithoutHealthBar) then --not critical code\n            \n            --there's two ways of showing this for friendly npcs (selected from the options panel): show all names or only npcs with profession names\n            --enemy npcs always show all\n            if (plateConfigs.all_names) then\n                --if this is an enemy or neutral npc\n                if (plateFrame.namePlateUnitReaction <= 4) then\n                    \n                    local r, g, b, a\n                    \n                    --get the quest color if this npcs is a quest npc\n                    if (plateFrame.namePlateIsQuestObjective) then\n                        if (plateFrame.namePlateUnitReaction == 4) then\n                            r, g, b, a = unpack (plateConfigs.quest_color_neutral)\n                        else\n                            r, g, b, a = unpack (plateConfigs.quest_color_enemy)\n                            g = g + 0.1\n                            b = b + 0.1\n                        end\n                    else\n                        r, g, b, a = 1, 1, 0, 1 --neutral\n                        if (plateFrame.namePlateUnitReaction <= 3) then\n                            r, g, b, a = 1, .05, .05, 1\n                        end\n                    end\n                    \n                    plateFrame.ActorNameSpecial:SetTextColor (r, g, b, a)\n                    \n                else\n                    --it's a friendly npc\n                    plateFrame.ActorNameSpecial:SetTextColor (unpack (plateConfigs.big_actorname_text_color))\n                end\n            else\n                --scan tooltip to check if there's an title for this npc\n                local subTitle = Plater.GetActorSubName (plateFrame)\n                if (subTitle and subTitle ~= \"\" and not Plater.IsNpcInIgnoreList (plateFrame, true)) then\n                    if (not subTitle:match (\"%d\")) then --isn't level\n                        \n                        plateFrame.ActorNameSpecial:SetTextColor (unpack (plateConfigs.big_actorname_text_color))\n                    end\n                end\n            end\n            \n            return\n        end\n    end\n    \nend",
