["Nameplate Updated"] = "function (self, unitId, unitFrame, envTable, modTable)\n    \n    \n    if envTable._isConcerned ~= true and modTable.IfConcernedSpell(unitFrame) then\n        modTable.AddT (unitFrame)\n        envTable._isConcerned = true\n        --print(string.format(\"Spell [%s] spot, Num: %d\", unitFrame.castBar.SpellName, modTable._ConcernedNum))\n    end\n    \n    modTable.UpdateT(unitFrame)\nend",
["Initialization"] = "-- Mod Working Rules:\n-- All units are divided into 3 types - concerned, normal and unconcerned.\n-- Unconcerned unit's nameplate will no longer show.\n-- When any concerned units exist, the normal nameplates hide as well.\n-- If no concerned units exist, normal nameplates show as normal.\n\n-- 模组的工作准则：\n-- 所有单位被划分为，关注（concerned），普通和不关注（unconcerned）三种类型。\n-- 不关注的目标永远不会显示姓名版。\n-- 当任意关注目标存在时，普通姓名版也会影藏。\n-- 当没有任何关注目标存在时，普通姓名版就想平常一样显示。\n\nfunction (modTable)\n    \n    -- Concerned units. You can fill with name, lower name or unitid.\n    -- By default, explosive is concerned unit. So when it appears, any other normal nameplates will hide.\n    -- 关注的目标，可以填写名字或者ID。\n    -- 默认爆炸球是关注目标，所以当爆炸球出现时，所有其他普通的姓名版会影藏。\n    modTable.Concerned = {\n    }\n    for k,_ in pairs(modTable.config._concerned) do\n        modTable.Concerned[k] = true\n    end\n    \n    -- Unconcerned units. Rule same with concerned ones.\n    -- 不关注的目标，规则如上。\n    modTable.Unconcerned = {\n    }\n    for k,_ in pairs(modTable.config._unconcerned) do\n        modTable.Unconcerned[k] = true\n    end\n    \n    modTable.ConcernedSpells = {\n    }\n    for k,_ in pairs(modTable.config._concernedSpells) do\n        modTable.ConcernedSpells[k] = true\n    end\n    if modTable._ConcernedNum == nil then\n        --print(\"Plater Mod - Expanded Visibility --Init Con Num\")\n        modTable._ConcernedNum = 0\n        modTable._ConcernedUnits = {}\n    else\n        --print(\"Plater Mod - Expanded Visibility --Init Con Num Repeation\")\n    end\n    \n    \n    function modTable.UpdateT(unitFrame)\n        if (unitFrame.IsSelf) then\n            return\n        end\n        \n        -- 总是显示目标单位\n        if modTable.config._showOnTargeting and unitFrame.namePlateIsTarget then\n            unitFrame:Show()\n            return\n        end\n        \n        -- 影藏不关注的目标\n        if  modTable.IfUnconcerned(unitFrame) then\n            unitFrame:Hide()\n            return\n        end\n        \n        -- 显示标记单位\n        if modTable.config._showOnMark and Plater.GetRaidMark(unitFrame) ~= nil then\n            unitFrame:Show()\n            return\n        end\n        \n        -- 显示施法单位\n        if modTable.config._showOnCasting and (unitFrame.castBar.channeling ~= nil or unitFrame.castBar.casting ~= nil) then\n            unitFrame:Show()\n            return\n        end\n        \n        -- 没有关注目标时，显示普通单位\n        if  modTable._ConcernedNum < 1 then\n            unitFrame:Show()\n            return\n        end\n        \n        -- 有关注目标，但是单位正在释放关注法术\n        if modTable.IfConcernedSpell(unitFrame)  then\n            unitFrame:Show()\n            return\n        end\n        \n        -- 有关注目标了，影藏普通单位\n        if not modTable.IfConcerned(unitFrame) then\n            unitFrame:Hide()\n        end\n    end\n    \n    \n    function modTable.IfConcerned(unitFrame)\n        local res = \n        modTable.Concerned[unitFrame.namePlateUnitNameLower] or  \n        modTable.Concerned[unitFrame.namePlateUnitName] or \n        modTable.Concerned[unitFrame.namePlateNpcId]\n        return res\n    end\n    \n    function modTable.IfUnconcerned(unitFrame)\n        local res = \n        modTable.Unconcerned[unitFrame.namePlateUnitNameLower] or  \n        modTable.Unconcerned[unitFrame.namePlateUnitName] or \n        modTable.Unconcerned[unitFrame.namePlateNpcId]\n        return res\n    end\n    \n    function modTable.IfConcernedSpell(unitFrame)\n        local res = \n        modTable.ConcernedSpells[unitFrame.castBar.SpellID] or  \n        modTable.ConcernedSpells[unitFrame.castBar.SpellName]\n        return res and (unitFrame.castBar.channeling ~= nil or unitFrame.castBar.casting ~= nil)\n    end\n    \n    function modTable.AddT(unitFrame)\n        if Plater.IsPlayerTank() then\n            return\n        end\n        modTable._ConcernedUnits[unitFrame.namePlateUnitGUID] = unitFrame.namePlateUnitGUID\n        modTable._ConcernedNum = 1\n    end\n    \n    function modTable.RemoveT(unitFrame)\n        modTable._ConcernedUnits[unitFrame.namePlateUnitGUID] = nil\n        for k,v in pairs(modTable._ConcernedUnits) do\n            if v then\n                return\n            end\n        end\n        modTable._ConcernedNum = 0\n    end\n    \nend",
["Cast Stop"] = "function (self, unitId, unitFrame, envTable, modTable)\n    if envTable._isConcerned == true then\n        modTable.RemoveT (unitFrame)\n        envTable._isConcerned = false\n        --print(string.format(\"[%s]'s Spell loss, Num: %d\", unitFrame.namePlateUnitName, modTable._ConcernedNum))\n    end\nend",
